// эффект тряски
  $.fn.shake = function(intShakes, intDistance, intDuration) {
      this.each(function() {
          $(this).css("position","relative"); 
          for (var x=1; x<=intShakes; x++) {
          $(this).animate({left:(intDistance*-1)}, (((intDuration/intShakes)/4)))
      .animate({left:intDistance}, ((intDuration/intShakes)/2))
      .animate({left:0}, (((intDuration/intShakes)/4)));
      }
    });
  return this;
  };

  // функция для каунтера
  function timeToEvent(eventDate){
    var now = new Date();
    var output = '';
    var daystoED = Math.floor(Math.round(eventDate-now)/86400000);
    var hourstoED = 24 - now.getHours() - 1 + eventDate.getHours();
    var minutestoED = 60 - now.getMinutes() - 1 + eventDate.getMinutes();
    var secondstoED = 60 - now.getSeconds() - 1 + eventDate.getMinutes();
    if (secondstoED >= 60) {
      minutestoED++;
      secondstoED-=60;
    }
    if (minutestoED >= 60) {
      hourstoED++;
      minutestoED-=60;
    }
    if (hourstoED >= 24) {
      daystoED++;
      hourstoED-=24;
    }
    daystoED = (daystoED < 1) ? "0"+daystoED : daystoED;
    hourstoED = (hourstoED < 10) ? "0"+hourstoED : hourstoED;
    minutestoED = (minutestoED < 10) ? "0"+minutestoED : minutestoED;
    secondstoED = (secondstoED < 10) ? "0"+secondstoED : secondstoED;      
    //output = daystoED+" дн. "+hourstoED+" час. "+minutestoED+" мин. "+secondstoED+" сек.";
    output = daystoED+' '+hourstoED+' '+minutestoED+' '+secondstoED;
    return output;
  }




