// настройка формы
function setupAjaxForm(form_id, form_validations){
    var form = '#' + form_id;
    var form_message = form + '-message';
 
    // включить/отключить кнопу отправки
    var disableSubmit = function(val){
        $(form + ' button[type=submit]').attr('disabled', val);
    };
 
    // Настройка сообщения "loading"
    $(form).ajaxSend(function(){
        $(form_message).removeClass().addClass('loading').html('Loading...').fadeIn();
    });
 
    // Настройка плагина JQuery 'ajaxForm'
    var options = {
        dataType:  'json',
        beforeSubmit: function(){
            // Запустить проверку формы если она существует
            if(typeof form_validations == "function" && !form_validations()) {
              return false;
            }
            disableSubmit(true);
        },
        success: function(json){
            /**
               * TОтвет на запрос AJAX будет выглядеть примерно так:
               * {"type" : "success", "messages" : "Thank-You for submitting the form!"}
               * После плагин формы получает ответ он анализирует строку в объекте JavaScript, позволяющий получить доступ к членам объекта, как показано ниже
               */
            $(form_message).hide();
            $(form_message).removeClass().addClass(json.type).html(json.message).fadeIn('slow');
            disableSubmit(false);
            if(json.type == 'success')
                $(form).clearForm();
        }
    };
    $(form).ajaxForm(options);
}











